using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using SideChicksRestApi.Data;
using SideChicksRestApi.Models;
using EntityState = Microsoft.EntityFrameworkCore.EntityState;

namespace SideChicksRestApi.Controllers
{
    [Route("api/[controller]")]
    [ApiController]
    public class ShowsController : ControllerBase
    {
        
        private readonly ApplicationDbContext _context;
        private static readonly DateTime later = DateTime.UtcNow.AddDays(40);
        
        public ShowsController(ApplicationDbContext context)
        {
            _context = context;
            var showsList = context.Shows.ToList();
            if (showsList.Count == 0)
            {
                SeedShows(_context);
            }
        }
        
        // GET: api/Shows
        [HttpGet]
        public async Task<List<Show>> Get()
        {
            return await _context.Shows.OrderBy(s => s.Id).ToListAsync();
        }

        // GET: api/Shows/5
        [HttpGet("{id}", Name = "GetShow")]
        public async Task<IActionResult> Get(int id)
        {
            var show = await _context.Shows.FindAsync(id);
            return show == null ? NotFound() : Ok(show);
        }

        // POST: api/Shows
        [HttpPost]
        public async Task<Show> Create(Show show)
        {
            
            _context.Add(show);
            await _context.SaveChangesAsync();

            return show;
        }

        // PUT: api/Shows/5
        [HttpPut("{id}")]
        public async Task<IActionResult> Update(int id, Show show)
        {
            if (id != show.Id) return BadRequest();

            var showOld = await _context.Shows.FindAsync(id);
            _context.Shows.Remove(showOld);
            _context.Shows.Add(show);
            
            await _context.SaveChangesAsync();
            return NoContent();

        }

        // DELETE: api/Shows/5
        [HttpDelete("{id}")]
        public async Task<IActionResult> Delete(int id)
        {
            var show = await _context.Shows.FindAsync(id);
            _context.Shows.Remove(show);
            await _context.SaveChangesAsync();
            return NoContent();
        }
      
        public static void SeedShows(ApplicationDbContext context)
        {

            try
            {
                var userList = context.Users.ToList();
                if (userList.Count == 0)
                {
                    UserController.SeedUsers(context);
                    userList = context.Users.ToList();
                }
                
                var locationList = context.Locations.ToList();
                if (locationList.Count == 0)
                {
                    LocationsController.SeedLocations(context);
                    locationList = context.Locations.ToList();
                }

                    
               
                    
                var adminUser = userList.Find(u => u.UserName is "admin");
                var user = userList.Find(u => u.UserName is "user");
                
                var AdminTestLocation = locationList.Find(l => l.Name is "Barbier Breda");
                var UserTestLocation = locationList.Find(l => l.Name is "Mad Molly's Irish Pub");

                var showsList = new List<Show>();
                
                var show = new Show()
                {
                    UserId = adminUser.Id,
                    Name = "Showcase for the App",
                    Image = "data:image/jpeg;base64,",
                    Details = "TestingPurposes only",
                    Fee = 5.0,
                    LocationId = AdminTestLocation.Id,
                    Date = later,
                    Accepted = true
                };
                showsList.Add(show);
                
                show = new Show()
                {
                    UserId = adminUser.Id,
                    Name = "another one",
                    Image = "data:image/jpeg;base64,",
                    Details = "TestingPurposes only",
                    Fee = 5.0,
                    LocationId = AdminTestLocation.Id,
                    Date = later,
                    Accepted = true
                };
                showsList.Add(show);

                show = new Show()
                {
                    UserId = user.Id,
                    Name = "Not accepted showcase",
                    Image = "data:image/jpeg;base64,",
                    Details = "TestingPurposes only",
                    Fee = 5.0,
                    LocationId = UserTestLocation.Id,
                    Date = later,
                    Accepted = false
                };
                showsList.Add(show);

                show = new Show()
                {
                    UserId = user.Id,
                    Name = "Another Not accepted showcase",
                    Image = "data:image/jpeg;base64,",
                    Details = "TestingPurposes only",
                    Fee = 5.0,
                    LocationId = UserTestLocation.Id,
                    Date = later,
                    Accepted = false
                };
                
                showsList.Add(show);
                context.Shows.AddRange(showsList);
                context.SaveChanges();
                
            }
            catch
            {
                throw new InvalidOperationException();
            }

        }
    }
}
